CREATE TABLE processing_metadata(id INTEGER DEFAULT(1) PRIMARY KEY, last_processed_at TIMESTAMP);;
CREATE TABLE vault(file_path VARCHAR UNIQUE, md_content VARCHAR, spr_content VARCHAR, embeddings_openai FLOAT[1536], embeddings_spr_custom FLOAT[1024], title VARCHAR, short_title VARCHAR, description VARCHAR, tags VARCHAR[], authors VARCHAR[], date DATE, pinned BOOLEAN, hide_frontmatter BOOLEAN, hide_title BOOLEAN, hide_on_sidebar BOOLEAN, hiring BOOLEAN, featured BOOLEAN, draft BOOLEAN, social VARCHAR[], github VARCHAR, website VARCHAR, avatar VARCHAR, discord_id VARCHAR, aliases VARCHAR[], icy DOUBLE, bounty DOUBLE, PICs VARCHAR[], status VARCHAR, "function" VARCHAR, estimated_tokens BIGINT, total_tokens BIGINT, should_deploy_perma_storage BOOLEAN, perma_storage_id VARCHAR, should_mint BOOLEAN, minted_at DATE, token_id VARCHAR, previous_paths VARCHAR[], ai_summary BOOLEAN, ai_generated_summary VARCHAR[], has_redirects BOOLEAN, redirect VARCHAR[]);;

